import React from 'react';
import { Card } from 'antd';
import { RetweetOutlined } from '@ant-design/icons';

const PostCard = (post) => {
  return (
    <div>
      <Card
        cover ={post.Images[0] && <PostImages images={post.Images}/>}
        actions={[
          <RetweetOutlined/>
        ]}
      >
        <Image/>
        <Content/>
        <Buttons></Buttons>
      </Card>
      <CommentForm/>
      <Comments/>
    </div>
  )
}
export default PostCard;
